import React, {useState} from 'react';
import {Button, Card, Container, Form, Row} from 'react-bootstrap';

const CourseCard = ({course}) => {
    return (
        <Card style={{width: "20%"}}
              className="m-2">
            <Card.Img variant="top" src={course.image}/>
            <Card.Body>
                <Card.Title>{course.title}</Card.Title>
                <Card.Text>{course.description}</Card.Text>
                <Card.Text>Level: {course.level}</Card.Text>
                <Card.Text>Direction: {course.direction}</Card.Text>
                <Button variant="primary">Go to Course</Button>
            </Card.Body>
        </Card>
    );
};

const CourseSelection = () => {
    const courses = [
        {
            id: 1,
            title: 'Java Programming',
            description: 'Learn Java from beginner to advanced level',
            image: 'data:image/png;base64,',
            language: 'Java',
            level: 'Beginner',
            direction: 'Backend Development',
        },
        {
            id: 2,
            title: 'Python Programming',
            description: 'Learn Python from beginner to advanced level',
            image: 'data:image/jpeg;base64,',
            language: 'Python',
            level: 'Beginner',
            direction: 'Data Science',
        },
        {
            id: 3,
            title: 'JavaScript Fundamentals',
            description: 'Master the basics of JavaScript programming',
            image: 'data:image/png;base64,',
            language: 'JavaScript',
            level: 'Intermediate',
            direction: 'Frontend Development',
        },
        {
            id: 4,
            title: 'C++ Programming',
            description: 'Explore the world of C++ programming language',
            image: 'data:image/png;base64,',
            language: 'C++',
            level: 'Intermediate',
            direction: 'Systems Programming',
        },
        {
            id: 5,
            title: 'Ruby on Rails Web Development',
            description: 'Build web applications using Ruby on Rails framework',
            image: 'data:image/png;base64,',
            language: 'Ruby',
            level: 'Advanced',
            direction: 'Full-Stack Development',
        }
    ];

    const [filteredCourses, setFilteredCourses] = useState(courses);
    const [selectedFilters, setSelectedFilters] = useState({language: '', level: '', direction: ''});

    const handleFilterChange = (filterName, value) => {
        setSelectedFilters((prevFilters) => ({
            ...prevFilters,
            [filterName]: value,
        }));
    };

    // Функція для фільтрації курсів за вибраними фільтрами
    const filterCourses = (courses, filters) => {
        return courses.filter((course) => {
            if (filters.language && filters.language !== 'All' && course.language !== filters.language) {
                return false;
            }
            if (filters.level && filters.level !== 'All' && course.level !== filters.level) {
                return false;
            }
            if (filters.direction && filters.direction !== 'All' && course.direction !== filters.direction) {
                return false;
            }
            return true;
        });
    };

    // Оновлення відфільтрованих курсів при зміні фільтрів
    React.useEffect(() => {
        const filteredCourses = filterCourses(courses, selectedFilters);
        setFilteredCourses(filteredCourses);
    }, [selectedFilters]);

    return (
        <div>
            <h2>Courses</h2>
            <div className="row">
                <div className="col-md-3">
                    <Card>
                        <Card.Body>
                            <Card.Title>Filters:</Card.Title>
                            <Form>
                                <Form.Group controlId="languageFilter">
                                    <Form.Label>
                                        <strong>Language:</strong>
                                    </Form.Label>
                                    <div>

                                        <Form.Check
                                            type="checkbox"
                                            id="languageAllFilter"
                                            label="All"
                                            checked={selectedFilters.language === 'All'}
                                            onChange={() => handleFilterChange
                                            ('language', 'All')}
                                        />
                                        {[...new Set(courses.map((course)=>course.language))].map((language) => (
                                            <Form.Check
                                                type="checkbox"
                                                id={language}
                                                label={language}
                                                checked={selectedFilters.language === language}
                                                onChange={() => handleFilterChange('language', language)}
                                            />
                                        ))}
                                    </div>
                                </Form.Group>
                                <Form.Group controlId="levelFilter">
                                    <Form.Label>
                                        <strong>Level:</strong>
                                    </Form.Label>
                                    <div>
                                        <Form.Check
                                            type="checkbox"
                                            id="levelAllFilter"
                                            label="All"
                                            checked={selectedFilters.level === 'All'}
                                            onChange={() => handleFilterChange('level', 'All')}
                                        />
                                        {[...new Set(courses.map((course)=>course.level))].map((level) => (
                                            <Form.Check
                                                type="checkbox"
                                                id={level}
                                                label={level}
                                                checked={selectedFilters.level === level}
                                                onChange={() => handleFilterChange('level', level)}
                                            />
                                        ))}
                                    </div>
                                </Form.Group>
                                <Form.Group controlId="directionFilter">
                                    <Form.Label>
                                        <strong>Direction:</strong>
                                    </Form.Label>
                                    <div>
                                        <Form.Check
                                            type="checkbox"
                                            id="directionAllFilter"
                                            label="All"
                                            checked={selectedFilters.direction === 'All'}
                                            onChange={() => handleFilterChange('direction', 'All')}
                                        />
                                        {[...new Set(courses.map((course)=>course.direction))].map((direction) => (
                                            <Form.Check
                                                type="checkbox"
                                                id={direction}
                                                label={direction}
                                                checked={selectedFilters.direction === direction}
                                                onChange={() => handleFilterChange('direction', direction)}
                                            />
                                        ))}
                                    </div>
                                </Form.Group>
                            </Form>
                        </Card.Body>
                    </Card>
                </div>
                <div className="col-md-9">
                    <Container className='p-4'>
                        <Row>
                            {
                                filteredCourses.map((course) => (
                                    <CourseCard key={course.id} course={course}/>
                                ))}
                        </Row>
                    </Container>
                </div>
            </div>
        </div>
    );
};

export default CourseSelection;